INSERT INTO `populars` (`id`, `name`, `nameBangla`, `national_flag`, `banner`, `content`, `contentBangla`, `active`, `created_at`, `updated_at`) VALUES
(3, 'Canada', 'কানাডা', 'public/uploads/ZwnxKUhyNWgWunF0xMpu1Yrp7vxRCGvu0XHA0fii.png', 'public/uploads/c2w1YKj0b4geZyXkyajD9IVSwSpU8yDvmVjVhAce.jpg', '<p style=\"text-align:center\"><span style=\"font-size:28px\"><strong><span style=\"font-family:Calibri,&quot;sans-serif&quot;\">Why Study in Canada&nbsp; ?</span></strong></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:14.0pt\">High Visa success rate</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:14.0pt\">World-class living standard </span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:14.0pt\">Scholarships available on merit-based </span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:14.0pt\">Part-time work opportunities </span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:14.0pt\">Easy &amp; State Gov. supported Permanent Residency (PR) </span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:14.0pt\">Get visa with minimum tuition deposit </span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:14.0pt\">Spouse visa available for the partner of study visa holder</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Nirmala UI&quot;,&quot;sans-serif&quot;\">Our </span></span><span style=\"font-size:14.0pt\">Canada office maintains direct liaison with university and lawyer</span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px\">&nbsp;</p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><strong><span style=\"font-size:14.0pt\">Requirements for Canada Study Visa:</span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><strong><span style=\"font-size:14.0pt\">Academic &ndash;</span></strong><br />\r\n<span style=\"font-size:14.0pt\">Certificate &amp; Marksheet<br />\r\nS.S.C/H.S.C/Bachelors/Masters (Equivalent) </span></span></span><br />\r\n<span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:14.0pt\"><strong>English Proficiency &ndash;</strong> IELTS &nbsp;/ PTE / TOEFL </span></span></span><br />\r\n<span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:14.0pt\"><strong>Passport</strong> </span><strong><span style=\"font-size:14.0pt\">&ndash; </span></strong><span style=\"font-size:14.0pt\">&nbsp;12 Months Validity</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><strong><span style=\"font-size:14.0pt\">Picture &ndash; </span></strong><span style=\"font-size:14.0pt\">35 x 45 c.m. (White background)</span></span></span><br />\r\n<span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><span style=\"font-size:14.0pt\"><strong>Sponsor &ndash; </strong>Last 6 Months bank statement, Bank Solvency Certificate, Source of Income, Last 2 years Tax history &nbsp;etc.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><strong><span style=\"font-size:14.0pt\">Visa Application Documents &ndash; </span></strong><span style=\"font-size:14.0pt\">&nbsp;Visa Applicati